<template>
  <v-dialog
    v-model="isVisibleShareLetterModal"
    @click:outside="handleCloseModal"
    max-width="640"
  >
    <v-card color="amber lighten-5">
      <v-card-title>
        <span class="m-font">レターをシェアする</span>
        <v-icon>mdi-email-edit-outline</v-icon>
      </v-card-title>
      <v-divider />
      <!-- シェア項目1 -->
    <div v-for="(letterTitle, index) in letterTitles()" :key="index">
      <v-col class="text-center mt-4">
        <div
          class="s-font"
        >{{ letterTitle.message }}</div>
<!-- ボタンを押したら項目に応じたデータを指定しSVGタグ側のデータに反映させる -->
        <v-card-actions class="justify-center">
          <v-btn
            color="blue"
            class="white--text"
            x-small
            :href="twitterShare()"
          >
            <v-icon>mdi-twitter</v-icon>
            シェアする
          </v-btn>
        </v-card-actions>
        <v-card-actions class="justify-center">
          <v-btn
            color="#FCFCFC"
            x-small
            @click="svgToPng(letterTitle)"
          >
            <v-icon>mdi-sync</v-icon>
            変換する
          </v-btn>
        </v-card-actions>
      </v-col>
    </div>
      <!-- 閉じる -->
      <v-row
        justify="center"
        class="ma-4"
      >
        <v-card-actions>
          <v-btn
            large
            @click="handleCloseModal"
          >
            閉じる
          </v-btn>
        </v-card-actions>
      </v-row>
