<script>
import axios from "axios";
import { mapGetters } from "vuex"
const svg2imageData = (svgElement, successCallback, errorCallback) => {
  const canvas = document.createElement("canvas");
  canvas.width = 614;
  canvas.height = 300;
  const ctx = canvas.getContext("2d");
  const image = new Image();
  image.onload = () => {
    ctx.drawImage(image, 0, 0, 614, 300);
    successCallback(canvas.toDataURL());
  };
  image.onerror = e => {
    errorCallback(e);
  };
  const svgData = new XMLSerializer().serializeToString(svgElement);
  image.src = "data:image/svg+xml;charset=utf-8;base64," + btoa(unescape(encodeURIComponent(svgData)));
};

export default {
  name: "ShareLetterModal",
  props: {
    isVisibleShareLetterModal: {
      type: Boolean,
      required: true,
    },
    receivedLetter: {
      type: Object,
      required: true
    },
  },
  data() {
    return {
      image_url: '',
      title: '',
      content: ''
    }
  },
  computed: {
    ...mapGetters({ currentUser: "users/currentUser" }),
  },
  methods: {
    handleCloseModal() {
      this.$emit("close-modal");
    },
    twitterShare() {
      const url = `https://goenbako.com/${this.currentUser.twitter_id}`
      return `https://twitter.com/share?text=${this.receivedLetter.sender.name}さん からファンレターが届いたよ！%0a&url=${url}&hashtags=ご縁箱`;
    },
    async svgToPng(letterTitle) {
      await this.addLetterContent(letterTitle);
      svg2imageData(this.$refs.svgArea, data => {
        console.log(data);
        document.getElementById('converted-image').src = data;
        this.shareImage.image_url = data;
      }, function (error) {
        console.log(error);
        alert('failed to image');
      });
    },
    letterTitles() {
      return [
        { message: '出会いのきっかけ・当時の印象', content: `${this.receivedLetter.letter.past}`, topic: "past" },
        { message: '現在の印象・どんな人？', content: `${this.receivedLetter.letter.current}`, topic: "current" },
        { message: '聞いてみたいこと／これから話してみたいこと',  content: `${this.receivedLetter.letter.future}`, topic: "future" },
        { message: `${this.receivedLetter.receiver.name}さんに期待していること`, content: `${this.receivedLetter.letter.expect}`, topic: "expect" },
        { message: 'メッセージ', content: `${this.receivedLetter.letter.message}`, topic: "message" }
      ]
      .filter(existsLetterItem => existsLetterItem.content)
      .map(existsLetterItem => existsLetterItem)
    },
    addLetterContent(letterTitle) {
      this.title = letterTitle.message
      this.content = letterTitle.content
    }
  },
};
</script>

